const hamburgerButton=document.querySelector(".hamburger-menu__button"),navMenu=document.querySelector(".hamburger-menu__nav");function closeNavMenu(){hamburgerButton.classList.remove("is-active"),navMenu.classList.remove("is-active")}hamburgerButton.addEventListener("click",(function(){this.classList.toggle("is-active"),navMenu.classList.toggle("is-active")})),document.addEventListener("keydown",(function(e){"Escape"===e.key&&navMenu.classList.contains("is-active")&&closeNavMenu()}));const textarea=document.querySelector(".message__input textarea"),inputBtn=document.querySelector(".message__input-btn svg");textarea.addEventListener("input",(()=>{""!==textarea.value.trim()?inputBtn.classList.add("active"):inputBtn.classList.remove("active")}));